mapper.update:


	SELECT C_NO 
	FROM TBL_COUNTRY
	WHERE U_NO = #{uNo}
	AND C_NAME = #{cName}



UPDATE TBL_COUNTRY
SET 
	C_NAME = #{cName},
	C_RATIO = #{cRatio},
	C_UPDATEDATE = to_char(sysdate, 'YYYY/MM/DD HH24:MI:SS')
WHERE C_NO = #{cNo}

mapper.delete:

DELETE TBL_COUNTRY
WHERE U_NO = #{uNo}
AND C_NAME = #{cName}
